mapper.update:
UPDATE REVIEWBOARD
SET
MEMBER_NO=#{member_no},
REVIEWBOARD_TITLE=#{reviewboard_title},REVIEWBOARD_CONTENT=#{reviewboard_content},CLASS_NAME=#{class_name},REVIEWBOARD_TE=#{reviewboard_te},REVIEWBOARD_STAR=#{reviewboard_star}
WHERE REVIEWBOARD_NO=#{reviewboard_no}

mapper.select:
SELECT * 
	FROM (
		SELECT ROWNUM RN, A.* 
			FROM (
					SELECT * 
					FROM REVIEWBOARD 
					ORDER BY REVIEWBOARD_NO DESC 
					) A
			)
WHERE RN BETWEEN #{start} AND #{end}
